package shejimoshi.chuangjianmoshi.yuanxing.shenkelong;import java.io.Serializable;/** * @Title: Citation * @Package shejimoshi.chuangjianmoshi.yuanxing * @Author: dingyunkai * @Description * @CreateTime: 2023/6/3 20:34 */public class Citation implements Cloneable, Serializable {    private Student student;    public void show(){        System.out.println(student.getName()+ "获得三好学生");    }    public Student getStudent() {        return student;    }    public void setStudent(Student student) {        this.student = student;    }    @Override    protected Citation clone() throws CloneNotSupportedException {        return (Citation) super.clone();    }}