package shejimoshi.jiegouxingmoshi.shipeiqi.object_adapter;import shejimoshi.jiegouxingmoshi.shipeiqi.SdCard;import shejimoshi.jiegouxingmoshi.shipeiqi.TfCard;import shejimoshi.jiegouxingmoshi.shipeiqi.TfCardImpl;/** * @Title: SdAdapterTf * @Package shejimoshi.jiegouxingmoshi.shipeiqi * @Author: dingyunkai * @Description * @CreateTime: 2023/6/4 18:56 */public class SdAdapterTf  implements SdCard {    private TfCard tfCard;    private SdAdapterTf(TfCard tfCard){        this.tfCard = tfCard;    }    @Override    public String read() {        System.out.println("adapter read tf card");        return tfCard.readTf();    }    @Override    public void writeSd(String msg) {        System.out.println("adapter write tf card");        tfCard.writeTf(msg);    }}