package shejimoshi.jiegouxingmoshi.waiguanmoshi;/** * @Title: Light * @Package shejimoshi.jiegouxingmoshi.waiguanmoshi * @Author: dingyunkai * @Description * @CreateTime: 2023/6/5 21:05 */public class SmartAppliancesFaced {    private Light light;    private Tv tv;    private AirCondition airCondition;    public SmartAppliancesFaced(){        light = new Light();        tv = new Tv();        airCondition = new AirCondition();    }    public void say(String message){        if (message.contains("打开")){            on();        }else {            off();        }    }    private void on(){        light.on();        tv.on();        airCondition.on();    }    private void off(){        light.off();        tv.off();        airCondition.off();    }}