package shejimoshi.jiegouxingmoshi.xiangyuanmoshi;import java.util.HashMap;/** * @Title: BoxFactory * @Package shejimoshi.jiegouxingmoshi.xiangyuanmoshi * @Author: dingyunkai * @Description * @CreateTime: 2023/6/6 20:49 */public class BoxFactory {    private HashMap<String,AbstractBox> map;    private BoxFactory(){        map = new HashMap<>();        map.put("I",new IBox());        map.put("L",new LBox());        map.put("O",new OBox());    }    private static BoxFactory factory = new BoxFactory();    public static BoxFactory getInstance(){        return factory;    }    public AbstractBox getShape(String name){        return map.get(name);    }}