package shejimoshi.jiegouxingmoshi.zhuangshizhe;/** * @Title: Egg * @Package shejimoshi.jiegouxingmoshi.zhuangshizhe * @Author: dingyunkai * @Description * @CreateTime: 2023/6/5 19:44 */public class Bacon extends Garnish{    public Bacon(float price, String desc, FastFood fastFood) {        super(2, "培根", fastFood);    }    @Override    public float task() {        return getPrice() + getFastFood().task();    }    public String getDesc(){        return super.getDesc() + getFastFood().getDesc();     }}