package shejimoshi.jiegouxingmoshi.zhuangshizhe;/** * @Title: Egg * @Package shejimoshi.jiegouxingmoshi.zhuangshizhe * @Author: dingyunkai * @Description * @CreateTime: 2023/6/5 19:44 */public class Egg extends Garnish{    public Egg(FastFood fastFood) {        super(1, "几点", fastFood);    }    @Override    public float task() {        return getPrice() + getFastFood().task();    }    public String getDesc(){        return super.getDesc() + getFastFood().getDesc();    }}