package shejimoshi.xingweimoshi.guanchazhe;import java.util.ArrayList;import java.util.List;/** * @Title: SubscriptionSubject * @Package shejimoshi.xingweimoshi.guanchazhe * @Author: dingyunkai * @Description * @CreateTime: 2023/6/7 21:34 */public class SubscriptionSubject implements Subject{    private List<Observer> wxUserList = new ArrayList<>();    @Override    public void attach(Observer observer) {        wxUserList.add(observer);    }    @Override    public void delete(Observer observer) {        wxUserList.remove(observer);    }    @Override    public void notfiy(String messeeage) {        for (Observer observer : wxUserList) {            observer.update(messeeage);        }    }}