package shejimoshi.xingweimoshi.guanchazhe;/** * @Title: WxUser * @Package shejimoshi.xingweimoshi.guanchazhe * @Author: dingyunkai * @Description * @CreateTime: 2023/6/7 21:36 */public class WxUser implements Observer{    private String name;    public WxUser(String name) {        this.name = name;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    @Override    public void update(String message) {        System.out.println(name + ";" + message);    }}