package shejimoshi.xingweimoshi.guanchazhe.jdk;import java.util.Observable;import java.util.Observer;/** * @Title: Policemen * @Package shejimoshi.xingweimoshi.guanchazhe.jdk * @Author: dingyunkai * @Description * @CreateTime: 2023/6/7 21:49 */public class Policemen implements Observer {    private String name;    public Policemen(String name) {        this.name = name;    }    public void setName(String name) {        this.name = name;    }    public String getName() {        return name;    }    @Override    public void update(Observable o, Object arg) {        System.out.println("警察：" + ((Thief) o).getName() + "，我已经盯你很久了，你可以保持沉默，但你所说的将成为呈堂证供！！！");    }}