package shejimoshi.xingweimoshi.minglingmoshi;import java.util.Map;/** * @Title: OrderCommand * @Package shejimoshi.xingweimoshi.minglingmoshi * @Author: dingyunkai * @Description * @CreateTime: 2023/6/7 20:08 */public class OrderCommand implements Command{    private SeniorChef receiver;    private order order;    public OrderCommand(SeniorChef receiver, shejimoshi.xingweimoshi.minglingmoshi.order order) {        this.receiver = receiver;        this.order = order;    }    @Override    public void execute() {        System.out.println(order.getDinintTable()+"桌的订单");        Map<String, Integer> hashMap = order.getHashMap();        for (Map.Entry<String, Integer> stringIntegerEntry : hashMap.entrySet()) {            receiver.makeFoot(stringIntegerEntry.getKey(),stringIntegerEntry.getValue());        }    }}