package shejimoshi.xingweimoshi.zerenlian;/** * @Title: LeaveRequest * @Package shejimoshi.xingweimoshi.zerenlian * @Author: dingyunkai * @Description * @CreateTime: 2023/6/7 20:28 */public class LeaveRequest {    private String name;    private Integer num;    private String  content;    public LeaveRequest(String name, Integer num, String content) {        this.name = name;        this.num = num;        this.content = content;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getNum() {        return num;    }    public void setNum(Integer num) {        this.num = num;    }    public String getContent() {        return content;    }    public void setContent(String content) {        this.content = content;    }}